using Microsoft.AspNetCore.Authentication;using Microsoft.AspNetCore.Identity;using SB_Admin_2_MVC.Consts;namespace SB_Admin_2_MVC.Configuration;public static class AuthAndAuth{    public static void ConfigureAuthentication(this WebApplicationBuilder builder)    {        builder.Services.AddAuthentication()            .AddGoogle(o =>            {                var googleAuthNSection = builder.Configuration.GetSection("Authentication:Google");                o.ClientId = googleAuthNSection["ClientId"];                o.ClientSecret = googleAuthNSection["ClientSecret"];                o.SignInScheme = IdentityConstants.ExternalScheme;                                o.ClaimActions.MapJsonKey(MvcUserClaimConsts.ProfilePicUrl, "picture", "url");                o.ClaimActions.MapJsonKey(MvcUserClaimConsts.Locale, "locale", "string");                // o.ClaimActions.MapJsonKey("urn:google:hd", "hd", "string"); // Not needed, right? Email is enough to understand if the user is from panjur or not.            });    }}